mapper.delete:
Delete from BucketTree_Member where
bucketTree_idx=#{bucketTree_idx} and
user_idx =#{user_idx}

mapper.select:
SELECT* FROM( SELECT *, ROW_NUMBER() OVER(ORDER BY name) as 행번호 FROM [User] WHERE idx IN (SELECT user_idx FROM [BucketTree_Member] WHERE bucketTree_idx = #{idx} AND STATE = 0) )subquery
WHERE 행번호 > (#{p.currentPage} - 1) * #{p.pageSize} AND 행번호 <= #{p.currentPage} * #{p.pageSize}
ORDER BY 행번호

SELECT* FROM( SELECT *, ROW_NUMBER() OVER(ORDER BY name) as 행번호 FROM [User] WHERE idx IN (SELECT user_idx FROM [BucketTree_Member] WHERE bucketTree_idx = #{idx} AND STATE = 1) )subquery
WHERE 행번호 > (#{p.currentPage} - 1) * #{p.pageSize} AND 행번호 <= #{p.currentPage} * #{p.pageSize}
ORDER BY 행번호


SELECT STATE
FROM BucketTree_Member
WHERE bucketTree_idx = #{bucketTree_idx} AND user_idx = #{user_idx}
