mapper.select:
SELECT * FROM ( SELECT *, CASE #{orderType} WHEN 1 THEN ROW_NUMBER() OVER(ORDER BY date desc) /*최신순*/ WHEN 2 THEN ROW_NUMBER() OVER(ORDER BY count desc) /*인기순*/ END row FROM [BucketList] WHERE IDX > 0 AND WRITER IN(0,1) AND ( (#{srchType}= 0) OR /*검색조건이 없을때 */ (#{srchType}= 1 AND CHARINDEX(#{srchText}, title) >= 1) OR /*질문 제목*/ (#{srchType}= 2 AND CHARINDEX(#{srchText}, title) >= 1 OR CHARINDEX(#{srchText}, contents) >= 1) OR /*질문 제목이나 내용*/ (#{srchType}= 3 AND #{srchText} IN (select name from [User])) ) /*질문 작성자*/
and ( (#{categoryType} = 0) OR (#{categoryType} = 1 AND [WHEN]=#{when} and [WHO]=#{who} and [WHAT] =#{what}) ) /*카테고리검색*/
)subquery WHERE row>(#{currentPage} - 1) * #{pageSize} and row <= (#{currentPage} *#{pageSize}) ORDER BY row

SELECT * FROM ( SELECT b.*, CASE #{orderType} WHEN 1 THEN ROW_NUMBER() OVER(ORDER BY b.date desc) /*최신순*/ WHEN 2 THEN ROW_NUMBER() OVER(ORDER BY b.count desc) /*인기순*/ END row FROM [BucketList] b WHERE b.IDX > 0 AND b.WRITER IN(0,1) AND ( (#{srchType}= 0) OR /*검색조건이 없을때 */ (#{srchType}= 1 AND CHARINDEX(#{srchText}, b.title) >= 1) OR /*질문 제목*/ (#{srchType}= 2 AND CHARINDEX(#{srchText}, b.title) >= 1 OR CHARINDEX(#{srchText}, b.contents) >= 1) OR /*질문 제목이나 내용*/ (#{srchType}= 3 AND #{srchText} IN (select name from [User])) ) /*질문 작성자*/
and ( (#{categoryType}=0) OR (#{categoryType}=1 AND [WHEN]=#{when} and [WHO]=#{who} and [WHAT] =#{what}) ) /*카테고리검색*/
) subquery
WHERE row > #{row} AND row <= #{row} + #{pageSize}
ORDER BY row

